INSERT INTO sso.sys_app_login_config (id, app_id, login_background_img, enable_background_blur, login_types, enable_protocol, protocol_content, protocol_content_en, enable_secrecy, secrecy_content, secrecy_content_en, send_welcome_email_way, welcome_email_content, login_page_theme, is_delete, create_time, update_time, create_user_id, update_user_id) VALUES (11, 20, 'https://cdn2.bigonelab.com/sso/upload-prod/app_login_bg/20220916133300390341.jpeg', false, '0,2', false, null, null, null, null, null, 0, null, 1, 0, '2022-09-16 13:33:07', null, 166, 166);
